      const height = parseInt(svg.style('height')) - margin.top - margin.bottom

      // x
      const x = d3.scale.ordinal()
        .domain(sortedDates)
        .rangePoints([0, width], 1)
      const xAxis = d3.svg.axis()
        .scale(x)
        .tickFormat(d => byMonth[d]['Event Month'])
      chart.append('g')
